-- For REIPDECLARE@DatasetName VARCHAR(200) = 'Clayton_K12StudentProgram_DS',@AliasName VARCHAR(200) = 'REIP',@NewAliasName VARCHAR(200) = 'REP',@TenantId INT =50BEGIN TRANSACTIONDECLARE @DomainRelatedViewId INTSelect @DomainRelatedViewId=DomainRelatedViewId FROM RptDomainRelatedViews WHERE DisplayName = @DatasetName and TenantId=@TenantIdUPDATE ReportDetails SET ReportFileDetails=REPLACE(ReportFileDetails,'"AliasName":"'+@AliasName+'"','"AliasName":"'+@NewAliasName+'"') WHERE DomainRelatedViewId=@DomainRelatedViewId and ReportFileDetails like '%"AliasName":"'+@AliasName+'"%' and TenantId=@TenantId;COMMIT TRANROLLBACK TRAN-- For REIP IndicatorDECLARE@DatasetName VARCHAR(200) = 'Clayton_K12StudentProgram_DS',@AliasName VARCHAR(200) = 'REIP Indicator',@NewAliasName VARCHAR(200) = 'REP Indicator',@TenantId INT =50BEGIN TRANSACTIONDECLARE @DomainRelatedViewId INTSelect @DomainRelatedViewId=DomainRelatedViewId FROM RptDomainRelatedViews WHERE DisplayName = @DatasetName and TenantId=@TenantIdUPDATE ReportDetails SET ReportFileDetails=REPLACE(ReportFileDetails,'"AliasName":"'+@AliasName+'"','"AliasName":"'+@NewAliasName+'"') WHERE DomainRelatedViewId=@DomainRelatedViewId and ReportFileDetails like '%"AliasName":"'+@AliasName+'"%' and TenantId=@TenantId;COMMIT TRANROLLBACK TRANselect * from ClaytonK12StudentProgramDSselect * from reportdetails where reportdetailsname like '%ell%'select * From RptViewFields where domainrelatedviewid = 161UPDATE RptViewFields SET DataType = 'varchar' WHERE RptViewFieldsId=4126